import React, { useState, useRef, useEffect } from 'react';
import Email from './images/envelope-open.svg';
import Telephone from './images/telephone-inbound.svg';
import Likes from './images/suit-heart.svg';
import Login from './images/person.svg';
import Signup from './images/person-plus.svg';
import './navbar.css';
import Logo from './images/logo.png'
import { Link } from 'react-router-dom';
import EmailFunction from './popupemailfunction';
import 'bootstrap/dist/css/bootstrap.min.css';

// search bar (list selected , list all)
// wishlist
// Signup, login 

export const NavBar = () => {
    const [isPopupOpen, setIsPopupOpen] = useState(false);
    const popupRef = useRef(null);

    const togglePopup = () => {
        setIsPopupOpen(!isPopupOpen);
    }

    const handleClickOutside = (event) => {
        if (popupRef.current && !popupRef.current.contains(event.target)) {
            setIsPopupOpen(false);
        }
    };

    useEffect(() => {
        if (isPopupOpen) {
            document.addEventListener('mousedown', handleClickOutside);
        } else {
            document.removeEventListener('mousedown', handleClickOutside);
        }

        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, [isPopupOpen]);


    function contactMethods() {

        return (
            <div className='navbar-outer-div'>
                <div className='navbar-inner-div'>
                    <div className='change-on-hover'>
                        <span onClick={togglePopup}><img src={Email} alt='' className='svg-image' /> SALES@RENTAWARE.COM</span>
                    </div>

                    <span>  |  </span>

                    <div className='change-on-hover'>
                        <span><img src={Telephone} alt='' className='svg-image' /> +65 8936 3356</span>
                    </div>
                </div>

                <div className='navbar-inner-div'>
                    <div className='change-on-hover'>
                        <span><img src={Likes} alt='' className='svg-image' /> My Wishlist</span>
                    </div>

                    <span>  |  </span>

                    <div className='change-on-hover'>
                        <span><img src={Login} alt='' className='svg-image' />Login</span>
                    </div>

                    <span>  |  </span>

                    <div className='change-on-hover'>
                        <span><img src={Signup} alt='' className='svg-image' />Sign Up</span>
                    </div>


                </div>

            </div>
        )
    }

    function mainNavbar() {
        return (
            <div>
                <div className='main-navbar-layout'>
                    <Link to="/cookware"><p>Cook</p></Link>

                    {/*include Link to Homepage App.js*/}
                    <Link to="/">
                        <img src={Logo} alt='' />
                    </Link>

                    <Link to="/bakeware"><p>Bake</p></Link>
                </div>

                <div>
                    {/* searchbar */}
                    <SearchBar />
                </div>

            </div>
        )
    }

    return (
        <div>
            {contactMethods()}
            {mainNavbar()}

            {/* pass togglePopup function to EmailFunction as a prop */}
            {isPopupOpen && (
                <EmailFunction closePopup={togglePopup} popupRef={popupRef} />
            )}

        </div>
    )
}

export const SearchBar = () => {

    const [keyword, setKeyword] = useState('');
    const [allListedProducts, setAllListedProducts] = useState([]);
    const [searchResults, setSearchResults] = useState([]);

    const handleSubmit = (e) => {
        e.preventDefault();
        handleSearch(allListedProducts);
    }

    const handleInputChange = (e) => {
        setKeyword(e.target.value);
    }

    console.log(keyword);

    useEffect(() => {

        const fetchProducts = () => {
            fetch('./products.json')
                .then(response => response.json())
                .then(data => {
                    const products = data.map(product => product);
                    setAllListedProducts(products);
                    if(keyword){
                        const newArray = data.filter(product => product.brand.toLowerCase().includes(keyword.toLowerCase()) || product.productName.toLowerCase().includes(keyword.toLowerCase())); 
                        setSearchResults(newArray); 
                    }
                })
                .catch(error => console.error('Error fetching products:', error));
        };

        fetchProducts();
    }, []);


    useEffect(() => {
        const lowercaseKeyword = keyword.toLowerCase(); 
        const newArrayy = allListedProducts.filter()
    },[keyword]); 


    console.log(allListedProducts);
    console.log(searchResults); 

    const handleSearch = (products) => {

        if (!Array.isArray(products)) {
            console.error('Expected an array of products');
            return;
        }

        let newSearchResultsProducts = [];
        const lowercaseKeyword = keyword.toLowerCase();

        products.forEach(product => {
            if (
                product.brand.toLowerCase().includes(lowercaseKeyword) || product.productName.toLowerCase().includes(lowercaseKeyword) || product.productDescription.toLowerCase().includes(lowercaseKeyword) || product.categories.toLowerCase().includes(lowercaseKeyword)
            ) {
                newSearchResultsProducts.push(product);
                console.log(newSearchResultsProducts); 
            }
        });
        setSearchResults(newSearchResultsProducts);

    }

    //create a function within component to use searchResults array
    function useSearchResults() {
        const theKeyword = keyword;
        const theSearchResultsArray = searchResults;
        return (
            <div>
                <p>The keyword: {theKeyword}</p>
                <p>The search results array: {theSearchResultsArray}</p>
            </div>
        )
    }

    return (
        <form className="form-inline my-2 my-lg-0" onSubmit={handleSubmit}>
            {useSearchResults()}
            <div className='searchbar-styling'>
                <input className="form-control mr-sm-2" type="search" placeholder="Search" value={keyword} onChange={handleInputChange}></input>
                <Link to="/searchresults">
                    <button className="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
                </Link>
            </div>
        </form>
    )
}

export default NavBar;
