import React, { useEffect, useState } from 'react';
import './rentalform.css';
import { json, useLocation, useNavigate } from 'react-router-dom';
import { keyboard } from '@testing-library/user-event/dist/keyboard';

const PurchaseForm = () => {
    const navigate = useNavigate(); // if form submit, go to purchase history log (not yet establiushed)
    const location = useLocation();
    const productInfoForPurchase = location.state?.productInfoForPurchase || []; //ok (from bakeware directly)
    // sessionStorage get productName (from rental page)


    // function *if navigate to anywhere other than clicking that 'Submit' button , delete sessionStorage 'transfer'
    // *if submit button clicked, useNavigate used, navigate away from page, also clear sessionStorage 'transfer'


    // 1: 
    console.log(JSON.stringify(productInfoForPurchase));

    // 2: 
    console.log(sessionStorage.getItem('transfer'));

    const [productInfo, setProductInfo] = useState([]);

    useEffect(() => {
        if (sessionStorage.getItem('transfer') !== null) {
            fetch('./products.json')
                .then(response => response.json())
                .then(data => {
                    const filteredInfo = data.filter(prdt =>
                        prdt.productName.includes(sessionStorage.getItem('transfer'))
                    );
                    console.log("impr: ", filteredInfo);
                    setProductInfo(filteredInfo);
                })
                .catch(error => console.error('Error fetching products:', error));
        } else {
            setProductInfo(JSON.stringify(productInfoForPurchase));

        }
    }, []);


    console.log("test if it works: ", productInfo);

    const clearTransferStorage = () => {
        sessionStorage.removeItem('transfer');
        console.log('sessionStorage key "transfer" has been cleared');
    };

    // Add event listener for beforeunload event
    window.addEventListener('beforeunload', clearTransferStorage);

    const {
        register,
        handleSubmit,
        formState: 
        setError
    } = useForm();

    const onPurchase = async (formData) => {

        // email is from localStorage

        try {
            const response = await axios.post('http://localhost:5000/api/purchase', {
                // email: formData.email,
                productId: productInfo.productID,
                productName: productInfo.productName,
                costPerDay: productInfo.rent,
                totalCosts: totalCosts,
                companyName: companyName,
                contactNumber: contactNumber,
                addressLine1: addressLine1,
                addressLine2: addressLine2
            });

        } catch (error) {
            if (error.response && error.response.status === 400) {
                console.error('Error:', error.response.data);
                setError('numberOfDays', { type: 'manual', message: error.response.data });
            } else {
                console.error('Error logging in:', error.response ? error.response.data : error.message);
            }
        }

    }

    return (
        <div>
            <h3>Purchase Form</h3>

            <div className='purchaseform-img-bg'>
                <img src={productInfo[0].imageURL}></img>
            </div>
            <h4>{productInfo[0].productName}</h4>

            <p>Selling Price: USD <span className='price-per-day'>{productInfo[0].price}</span></p>

            <form onSubmit={handleSubmit(onPurchase)}>


                <button type='submit'></button>
            </form>

        </div>
    )
}

export default PurchaseForm;
