import React, { useEffect, useState } from 'react';
import './rentalform.css';
import { useLocation, useNavigate } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import axios from 'axios';
import $ from 'jquery';

const PurchaseForm = () => {
    const navigate = useNavigate(); // if form submit, go to purchase history log 
    const location = useLocation();
    const productInfoForPurchase = location.state?.productInfoForPurchase || [];

    // 1: //
    console.log(JSON.stringify(productInfoForPurchase));

    // 2: //
    console.log(sessionStorage.getItem('transfer'));
    const keyword = sessionStorage.getItem('transfer');
    console.log("LOOKY LOOKIE: ", keyword);

    const [productInfo, setProductInfo] = useState([]);
    let filteredInfo = []; 

    if (keyword !== null) {
        fetch('./products.json')
            .then(response => response.json())
            .then(data => {
                console.log("Data fetched:", data);  // Debugging
                filteredInfo = data.filter(prdt =>
                    prdt.productName.toLowerCase().includes(keyword.toLowerCase())
                );
                console.log("impt: ", filteredInfo);
                // setProductInfo(filteredInfo);
            })
            .catch(error => console.error('Error fetching products:', error));
    } else {
        // setProductInfo(productInfoForPurchase);
        filteredInfo = productInfoForPurchase; 
    }


    // over here: 
    // console.log("test if it works: ", productInfo);
    console.log("TESt impt: ", filteredInfo); 

    const clearTransferStorage = () => {
        sessionStorage.removeItem('transfer');
        console.log('sessionStorage key "transfer" has been cleared');
    };

    // Add event listener for beforeunload event
    window.addEventListener('beforeunload', clearTransferStorage);

    const {
        register,
        handleSubmit,
        formState: { errors },
    } = useForm();

    const onPurchase = async (formData) => {

            // email is from localStorage
            const email = localStorage.getItem('email');

            try {
                const response = await axios.post('http://localhost:5000/api/purchase', {
                    email: email,
                    productId: productInfo.productID,
                    productName: productInfo.productName,
                    productPrice: productInfo.price,
                    selectedQty: purchaseQty,
                    totalAmount: formData.totalAmount,
                    companyName: formData.companyName,
                    contactNumber: formData.contactNumber,
                    addressLine1: formData.addressLine1,
                    addressLine2: formData.addressLine2
                });

            } catch (error) {
                if (error.response) {
                    console.error('Error:', error.response.data);
                }
            }

    }


    useEffect(() => {
        const $select = $(".p1-30");
        if ($select.children().length === 0) {
            for (let i = 1; i <= 30; i++) {
                $select.append($('<option></option>').val(i).html(i));
            }
        }
    }, []);


    const [purchaseQty, setPurchaseQty] = useState(1);

    const [totalAmount, setTotalAmount] = useState(filteredInfo[0].price);


    useEffect(() => {
        setTotalAmount(purchaseQty * filteredInfo[0].price);
    }, [purchaseQty, filteredInfo]);


    // Particulars of customer 
    const [companyName, setCompanyName] = useState([]);
    const [contactNumber, setContactNumber] = useState([]);
    const [addressLine1, setAddressLine1] = useState([]);
    const [addressLine2, setAddressLine2] = useState([]);


    return (
        <div className='purchase-form-bg'>
            <h3>Purchase Form</h3>

            <div className='purchaseform-img-bg'>
                <img src={filteredInfo[0].imageURL}></img>
            </div>
            <h4>{filteredInfo[0].productName}</h4>

            <p>Selling Price: USD <span className='price-per-day'>{filteredInfo[0].price}</span></p>

            <form onSubmit={handleSubmit(onPurchase)}>

                <div className='spacing-between-divisions flex-display'>
                    <p>Quantity: </p>
                    <select className="p1-30" onChange={(e) => setPurchaseQty(Number(e.target.value))}></select>
                </div>

                <p>Total costs: USD <input {...register("totalAmount", { required: "This is required" })} type='text' className='total-cost-purchase' value={totalAmount} readOnly></input>
                </p>

                <h5 style={{ paddingTop: '25px' }}>Particulars</h5>

                <p>Company Name: <input {...register("companyName", { required: "This is required" })} type='text' value={companyName} onChange={(e) => setCompanyName(e.target.value)} style={{ width: '100%' }}></input>

                </p>
                <p>Contact Number: <input {...register("contactNumber", { required: "This is required" })} placeholder="123-456-7890" type='tel' onChange={(e) => setContactNumber(e.target.value)} value={contactNumber}></input>

                </p>
                <p>Address Line 1: <input {...register("addressLine1", { required: "This is required" })} type='text' onChange={(e) => setAddressLine1(e.target.value)} value={addressLine1} style={{ width: '100%' }}></input>

                </p>
                <p>Address Line 2: <input {...register("addressLine2", { required: "This is required" })} type='text' onChange={(e) => setAddressLine2(e.target.value)} value={addressLine2} style={{ width: '100%' }}></input>

                </p>

                <div className='center spacing-between-divisions'>
                    <button type='submit' className='btn btn-warning'></button>
                </div>

            </form>

        </div>
    )
}

export default PurchaseForm;
