import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UserQuotations = () => {

    const [quotations, setQuotations] = useState([]);
    const [allProducts, setAllProducts] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            console.log('Making request...');
            try {
                const response = await axios.get('http://localhost:5000/api/accountinfo');
                console.log('Data retrieved: ', response.data);
                // setQuotations according to email logged in
                const emailLoggedIn = JSON.parse(localStorage.getItem('email'));
                setQuotations(response.data.filter(item => item.quotation_email.replace(/["\\]/g, '') === emailLoggedIn));
                console.log(quotations);
            } catch (error) {
                if (error.response && error.response.status === 400) {
                    console.error('Error:', error.response.data);
                } else {
                    console.error('Error logging in:', error.response ? error.response.data : error.message);
                }
            }
        };

        fetchData();
    }, []);


    const indexCount = (index) => {
        return index + 1;
    }

    const datePart = (string) => {
        return string.split('T')[0];
    }

    // Link painting (sec) to code 

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await fetch('./products.json');
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setAllProducts(data.productid_combination);
                getAssociatedProductNames(allProducts);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        }
        fetchProducts();
    }, []);


    let arrayOfProductNames = [];

    const getAssociatedProductNames = (arrayOfObjects) => {
        arrayOfProductNames = arrayOfObjects.filter(item => item.productID)
    }


    return (
        <div>
            <h1>Quotations</h1>

            <div className='white-bg'>
                <table>
                    <thead>
                        {quotations && quotations.length > 0 && (
                            <tr>
                                <th>Index</th>
                                <th>Quotation_ID</th>
                                <th>Date of Request</th>
                                <th>Product Names</th>
                            </tr>
                        )}
                    </thead>

                    <tbody>
                        {(quotations && quotations.length > 0) ? (
                            quotations.map((quotation, index) => (
                                <tr key={indexCount(index)}>

                                    <td>{indexCount(index)}</td>

                                    <td>{quotation.quotation_id}</td>

                                    <td>{datePart(quotation.date_of_creation)}</td>

                                    {/* retrieve each prdt name from products.json file */}
                                    <td>{quotation.productid_combination}</td>
                                </tr>
                            ))

                        ) : (
                            <p>No data available.</p>
                        )}
                    </tbody>
                </table>
            </div>

        </div >
    )
}

export default UserQuotations;
