import BakewareBackground from '../routes/components/bakeware.jpeg';
import './bakeware.css';
import NavbarComponents from '../components/navbar';
import { useState } from 'react';

const ContainerforBakeware = () => {
    return (
        <div>
            <DisplayAllBakeware />
        </div>
    )
}

const DisplayAllBakeware = () => {
    const [bakewareProducts, setBakewareProducts] = useState([]);

    useEffect(() => {
        fetch('./products.json')
            .then(response => response.json())
            .then(data => {
                const filteredBakewareProducts = data.filter(bakewareProduct => bakewareProduct.categories.split(',').includes('bakeware'));
                setBakewareProducts(filteredBakewareProducts);
            })
            .catch(error => console.error('Error fetching products:', error));
    }, []);


    return (
        <div>
            <h1>Cookware</h1>
            <ul>
                <div className='layout-cookware-listings'>
                    {cookwareProducts.map(cookwareProduct => (
                        <div className='layout-cookware-sixes' key={cookwareProduct.productID}>
                            {/* whenever 6 cookwares are displayed, next 6 are displayed on subsequent page */}
                            <li>

                                <div className='cookware-listings-image-container'>
                                    <img src={cookwareProduct.imageURL} alt='' />
                                </div>
                                <h2>{cookwareProduct.productName}</h2>
                                <p>{cookwareProduct.productDescription}</p>
                                <p>Add to Wishlist for quotation</p>
                                <button>Add to Wishlist</button>

                            </li>
                        </div>
                    ))}
                </div>
            </ul>
        </div>
    )
}

export default function Bakeware() {
    return (
        <div>
            <ContainerforBakeware />
            <div className='background-image-bakeware' style={{ backgroundImage: `url(${BakewareBackground})` }}></div>
        </div>
    )
}
