import React, { useState, useRef, useEffect } from 'react';
import Email from './images/envelope-open.svg';
import Telephone from './images/telephone-inbound.svg';
import Likes from './images/suit-heart.svg';
import Login from './images/person.svg';
import Signup from './images/person-plus.svg';
import './navbar.css';
import Logo from './images/logo.png'
import { Link } from 'react-router-dom';
import EmailFunction from './popupemailfunction';
import 'bootstrap/dist/css/bootstrap.min.css';

// search bar (list selected , list all)
// wishlist (product buttons)
// Signup, login 

const NavBar = () => {
    const [isPopupOpen, setIsPopupOpen] = useState(false);
    const popupRef = useRef(null);

    const togglePopup = () => {
        setIsPopupOpen(!isPopupOpen);
    }

    const handleClickOutside = (event) => {
        if (popupRef.current && !popupRef.current.contains(event.target)) {
            setIsPopupOpen(false);
        }
    };

    useEffect(() => {
        if (isPopupOpen) {
            document.addEventListener('mousedown', handleClickOutside);
        } else {
            document.removeEventListener('mousedown', handleClickOutside);
        }

        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, [isPopupOpen]);


    function contactMethods() {

        return (
            <div className='navbar-outer-div'>
                <div className='navbar-inner-div'>
                    <div className='change-on-hover'>
                        <span onClick={togglePopup}><img src={Email} alt='' className='svg-image' /> SALES@RENTAWARE.COM</span>
                    </div>

                    <span>  |  </span>

                    <div className='change-on-hover'>
                        <span><img src={Telephone} alt='' className='svg-image' /> +65 8936 3356</span>
                    </div>
                </div>

                <div className='navbar-inner-div'>
                    <div className='change-on-hover'>
                        <span><img src={Likes} alt='' className='svg-image' /> My Wishlist</span>
                    </div>

                    <span>  |  </span>

                    <div className='change-on-hover'>
                        <span><img src={Login} alt='' className='svg-image' />Login</span>
                    </div>

                    <span>  |  </span>

                    <div className='change-on-hover'>
                        <span><img src={Signup} alt='' className='svg-image' />Sign Up</span>
                    </div>


                </div>

            </div>
        )
    }

    function mainNavbar() {
        return (
            <div>
                <div className='main-navbar-layout'>
                    <Link to="/cookware"><p>Cook</p></Link>

                    <Link to="/">
                        <img src={Logo} alt='' />
                    </Link>

                    <Link to="/bakeware"><p>Bake</p></Link>
                </div>

                <div>
                    <SearchBar />
                </div>

            </div>
        )
    }

    return (
        <div>
            {contactMethods()}
            {mainNavbar()}

            {/* pass togglePopup function to EmailFunction as a prop */}
            {isPopupOpen && (
                <EmailFunction closePopup={togglePopup} popupRef={popupRef} />
            )}

        </div>
    )
}

const SearchBar = () => {

    const [keyword, setKeyword] = useState('');
    const [searchResults, setSearchResults] = useState([]);

    const handleInputChange = (e) => {
        setKeyword(e.target.value);
    }

    console.log(keyword);

    useEffect(() => {
        // fetch products with relation to keyword 
        const fetchProducts = async () => {
            fetch('./products.json')
                .then(response => response.json())
                .then(data => {
                    const products = data.filter(product => product.brand.toLowerCase().includes(keyword.toLowerCase()) || product.productName.toLowerCase().includes(keyword.toLowerCase()) || product.productDescription.toLowerCase().includes(keyword.toLowerCase()) || product.categories.toLowerCase().includes(keyword.toLowerCase()));
                    setSearchResults(products);
                })
                .catch(error => console.error('Error fetching products:', error));
        };

        fetchProducts();
    }, [keyword]);

    console.log(searchResults);


    return (
        <div>
            <div className='to-hide-from-view'>
                <SearchResultsList list={searchResults} />
            </div>
            <form className="form-inline my-2 my-lg-0">
                <div className='searchbar-styling'>
                    <input className="form-control mr-sm-2" type="search" placeholder="Search" value={keyword} onChange={handleInputChange}></input>
                    <Link to="/searchresults">
                        <button className="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
                    </Link>
                </div>
            </form >
        </div>
    )
}


const SearchResultsList = ({ list }) => {
    return (
        <div>
            {list.map((product, index) => (
                <div key={index}>
                    <h3>{product.productName}</h3> // Display the product name
                    <p>{product.productDescription}</p> // Display the product description
                    {/* Display more product details here if needed */}
                </div>
            ))}
        </div>
    )
}


export default NavBar;
