import React from 'react';
import './registerpage.css';
import { useForm } from "react-hook-form";
import { useNavigate } from "react-router-dom";
import axios from 'axios';

const Login = () => {
    const navigate = useNavigate();
    const {
        handleSubmit,
        formState: { errors },
        setError,
        clearErrors
    } = useForm();


    //this email address does not have an account. pls register new account.


    const onLogin = async (formData) => {
        const { email, password } = formData;

        try {
            const response = await axios.post('http://localhost:5000/api/login', {
                email,
                password
            });
            console.log('Login successful: ', response.data);
            navigate('/');
        } catch (error) {
            if (error.response) {
                console.error('Error logging in:', error.response.data);
                // Handle error responses (e.g., show error message to user)

            } else {
                console.error('Error:', error.message);
            }
        }
    };

    return (
        <div className='login-form-bg'>
            <h3>Login</h3>
            <form onSubmit={handleSubmit(onLogin)}>
                <div className='login-account-inputs'>
                    <h5>Your email</h5>
                    <input {...login("email", { required: "Email is required", pattern: /^\S+@\S+\.\S+$/ })} type='email' placeholder='xxx@mailbox.com' />
                    {errors.email && <span style={{ color: 'red' }}>{errors.email.message}</span>}
                </div>
                <div className='login-account-inputs'>
                    <h5>Your password</h5>
                    <input {...login("password", { required: "Password is required" })} type='password' />
                    {errors.password && <span style={{ color: 'red' }}>{errors.password.message}</span>}
                </div>
                <div className='login-center'>
                    <button type="submit" className="btn btn-success">LOG IN</button>
                </div>
            </form>
        </div>
    )
}

export default Login;
