import React, { useEffect } from 'react';
import './rentalform.css';
import $ from 'jquery';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Dropdown } from 'react-bootstrap';

const RentalRequest = () => {

    useEffect(() => {
        const $select = $(".1-30");
        if ($select.children().length === 0) {
            for (let i = 1; i <= 30; i++) {
                $select.append($('<option></option>').val(i).html(i));
            }
        }
    }, []);


    useEffect(() => {
        const $select = $(".start-day");
        if ($select.children().length === 0) {
            for (let j = 1; j <= 30; j++) {
                $select.append($('<option></option>').val(j).html(j));
            }
        }
    }, []);

    useEffect(() => {
        const $select = $(".start-day");
        if ($select.children().length === 0) {
            for (let j = 1; j <= 30; j++) {
                $select.append($('<option></option>').val(j).html(j));
            }
        }
    }, []);

    

    return (
        <div className='rental-form-bg'>

            <h2>Rental Request</h2>

            {/* info from popup button transfer */}

            <p>Price per day: <span></span></p>
            {/* info from products.json transfer */}

            <form>
                <div>
                    <label>Quantity:</label>
                    <select class="1-30"></select>
                </div>

                <div>
                    <label>Start Date:</label>
                    <select></select><select></select><select></select>
                </div>

                <div>
                    <label>End Date:</label>
                    <select></select><select></select><select></select>
                </div>

                <p>Number of days: <input></input></p>

                <p>Total cost of rental: USD <input></input></p>

                <button type='submit' className='btn btn-warning'>Submit</button>
            </form>

        </div>
    )
}

export default RentalRequest;
