import React from 'react';
import './registerpage.css';
import { useForm } from "react-hook-form";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";

const Register = () => {
    const navigate = useNavigate();
    const { register, handleSubmit, watch, formState: { errors } } = useForm();

    const onSubmit = async data => {
        console.log(data);
        //send this data to server 
        //navigate to another route 
        try {
            const response = await fetch('http://localhost:5000/register', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(data)
            });
            const responseData = await response.json();
            if (response.ok) {
                console.log(responseData.message); // "User registered successfully"
                // Navigate to another route upon successful registration
                navigate('/notification'); 
            } else {
                throw new Error(responseData.message);
            }
        } catch (error) {
            console.error('Registration failed:', error);
        }
    }

    return (
        <div className='register-form-bg'>
            <h3>Register your account</h3>
            <form onSubmit={handleSubmit(onSubmit)}>
                <div className='register-account-inputs'>
                    <h5>Your username</h5>
                    <input {...register('username', { required: true })} type='text' placeholder='Doe'></input>
                    {errors.username && <span style={{color: 'red'}}>This field is required</span>}
                </div>
                <div className='register-account-inputs'>
                    <h5>Your email</h5>
                    <input {...register('email', { required: true })} type='text' placeholder='xxx@mailbox.com'></input>
                    {errors.email && <span style={{color: 'red'}}>This field is required</span>}
                </div>
                <div className='register-account-inputs'>
                    <h5>Your password</h5>
                    <input {...register('password', { required: true })} type='password' placeholder=''></input>
                    {errors.password && <span style={{color: 'red'}}>This field is required</span>}
                </div>
                <div className='register-account-inputs'>
                    <h5>Repeat password</h5>
                    <input {...register('passwordRepeat', {
                        validate: value => value === watch('password') || 'Passwords do not match'
                    })} type='password' placeholder=''></input>
                    {errors.passwordRepeat && <span style={{color: 'red'}}>{errors.passwordRepeat.message}</span>}
                </div>
                <div className='register-center'>
                    <input {...register('terms', { required: true })} className="form-check-input" type="checkbox" id="checkboxNoLabel" value="" aria-label="..."></input>
                    <label className="form-check-label" for="inlineCheckbox1">I agree with the Terms & Conditions</label>
                    {errors.terms && <span style={{color: 'red'}}>You must agree to the terms</span>}
                </div>
                <div className='register-center'>
                    <button type="submit" className="btn btn-primary">Register Account</button>
                </div>
            </form>
        </div>
    )
}

export default Register;
