import React, { useState, useEffect } from 'react';
import './rentalform.css';
import $ from 'jquery';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useForm } from 'react-hook-form';
import axios from 'axios';

const RentalRequest = () => {

    const {
        register,
        handleSubmit,
        formState: { errors },
        setError,
    } = useForm();

    const onRentalRequest = async (formData) => {

        try {
            const response = await axios.post('http://localhost:5000/api/rentalrequest', {
                email: formData.email,
                // productId: product_id,
                // productName: product_name,
                startDate: startDate,
                endDate: endDate,
                numberOfDays: number_of_days,
                // costPerDay: costPerDay,
                // totalCosts: totalCosts,
                // paymentMethod: paymentMethod
            });

        } catch (error) {
            if (error.response && error.response.status === 400) {
                console.error('Error:', error.response.data);
                setError('numberOfDays', { type: 'manual', message: error.response.data });
            } else {
                console.error('Error logging in:', error.response ? error.response.data : error.message);
            }
        }

    }

    const dateNow = new Date();
    const dayNow = dateNow.getDate();
    const monthNow = dateNow.getMonth() + 1; // Months are zero-indexed in JS 
    const yearNow = dateNow.getFullYear();

    const [selectedDay, setSelectedDay] = useState(dayNow);
    const [selectedMonth, setSelectedMonth] = useState(monthNow);
    const [selectedYear, setSelectedYear] = useState(yearNow);

    const dateEnd = new Date();
    dateEnd.setDate(dateEnd.getDate() + 7);
    const dayEnd = dateEnd.getDate();
    const monthEnd = dateEnd.getMonth() + 1;
    const yearEnd = dateEnd.getFullYear();

    const [selectedEndDay, setSelectedEndDay] = useState(dayEnd);
    const [selectedEndMonth, setSelectedEndMonth] = useState(monthEnd);
    const [selectedEndYear, setSelectedEndYear] = useState(yearEnd);


    const days = Array.from({ length: 31 }, (_, i) => i + 1);

    const months = [
        "January", "February", "March", "April", "May", "June",
        "July", "August", "September", "October", "November", "December"
    ];

    const startYear = 2024;
    const endYear = 2034;
    const years = Array.from({ length: endYear - startYear + 1 }, (_, i) => startYear + i);


    useEffect(() => {
        const $select = $(".1-30");
        if ($select.children().length === 0) {
            for (let i = 1; i <= 30; i++) {
                $select.append($('<option></option>').val(i).html(i));
            }
        }
    }, []);


    // Function for making day,month,year into a date
    const formatDate = (day, month, year) => {
        const date = new Date(year, month - 1, day); // month is zero-indexed
        const options = { weekday: 'short', month: 'short', day: '2-digit', year: 'numeric' };
        return date.toLocaleDateString('en-US', options);
    }

    const mockup = formatDate(selectedDay, selectedMonth, selectedYear);
    console.log('This is printed ! : ', mockup);

    // Function for checking start date is before end date 
    const startDate = formatDate(selectedDay, selectedMonth, selectedYear);
    const endDate = formatDate(selectedEndDay, selectedEndMonth, selectedEndYear);
    let number_of_days = 7;
    if (endDate > startDate && endDate - startDate >= 7) {
        number_of_days = endDate - startDate;
    } else {
        console.error('Please make sure start date is before end date OR rental period at least 7 days.')
    }

    // Function for sending error message should rental period < 7 days 


    // Function for disabling days , months should the selection not be available for that month , year 


    // Function for advising users to purchase equipment instead of renting should total rental > cost 



    return (
        <div className='rental-form-bg'>

            <h3 className='center'>Rental Request</h3>

            {/* info from popup button transfer */}

            <p>Price per day: <span className='price-per-day'>{6}</span></p>
            {/* info from products.json transfer */}

            <form onSubmit={handleSubmit(onRentalRequest)}>
                <div>
                    <label>Quantity:</label>
                    <select className="1-30"></select>
                </div>

                <div className='space-between'>
                    <label>Start Date:</label>
                    <div>
                        <select className='start-day' value={selectedDay} onChange={(e) => setSelectedDay(Number(e.target.value))}>
                            {days.map(day => (
                                <option key={day} value={day}>{day}</option>
                            ))}
                        </select>
                        <select className='start-month' value={selectedMonth} onChange={(e) => setSelectedMonth(Number(e.target.value))}>
                            {months.map((month, index) => (
                                <option key={index + 1} value={index + 1}>{month}</option>
                            ))}
                        </select>
                        <select className='start-year' value={selectedYear} onChange={(e) => setSelectedYear(Number(e.target.value))}>
                            {years.map(year => (
                                <option key={year} value={year}>{year}</option>
                            ))}
                        </select>
                    </div>
                </div>

                <div>
                    <label>End Date:</label>
                    <div className='space-between'>
                        <select className='end-day' value={dayEnd} onChange={(e) => setSelectedEndDay(Number(e.target.value))}>
                            {days.map(day => (
                                <option key={day} value={day}>{day}</option>
                            ))}
                        </select>
                        <select className='end-month' value={monthEnd} onChange={(e) => setSelectedEndMonth(Number(e.target.value))}>
                            {months.map((month, index) => (
                                <option key={index + 1} value={index + 1}>{month}</option>
                            ))}
                        </select>
                        <select className='end-year' value={yearEnd} onChange={(e) => setSelectedEndYear(Number(e.target.value))}>
                            {years.map(year => (
                                <option key={year} value={year}>{year}</option>
                            ))}
                        </select>
                    </div>
                </div>

                <p>Number of days: <input {...register("numberOfDays", { required: "This is required" })} type='text' className='count-days-value' value='' readOnly></input>
                    {errors.numberOfDays && <span style={{ color: 'red' }}>{errors.numberOfDays.message}</span>}
                </p>

                <p>Total cost of rental: USD <input {...register("totalCosts", { required: "This is required" })} type='text' className='total-cost-rental' value={dateNow} readOnly></input>
                    {errors.totalCosts && <span style={{ color: 'red' }}>{errors.totalCosts.message}</span>}
                </p>

                <button type='submit' className='btn btn-warning'>Submit</button>
            </form>


        </div>
    )
}

export default RentalRequest;
