import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UserQuotations = () => {


    document.addEventListener('DOMContentLoaded', () => {
        fetch('/api/accountinfo')
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }
                return response.json(); // This will throw if the response is not JSON
            })
            .then(data => {
                const dataContainer = document.getElementById('data-container');
                data.forEach(row => {
                    const rowDiv = document.createElement('div');
                    rowDiv.textContent = JSON.stringify(row);
                    dataContainer.appendChild(rowDiv);
                });
            })
            .catch(error => {
                console.error('Error:', error.message);
                if (error.message.includes('Unexpected token')) {
                    console.error('The server returned a non-JSON response. Please check the server logs for more details.');
                }
            });
    });
    

    // document.addEventListener('DOMContentLoaded', async () => {
    //     fetch('/data', { timeout: 5000 })
    //     try {
    //         const response = axios.get('http://localhost:3001/api/accountinfo', {
    //             timeout: 5000, // Set timeout to 5 seconds
    //         })
    //         console.log('Account Data:', response.data);
    //     } catch (error) {
    //         if (error.response) {
    //             // Server responded with a status other than 200 range
    //             console.error('Response error:', error.response.data);
    //         } else if (error.request) {
    //             // Request was made but no response received
    //             console.error('No response:', error.request);
    //         } else {
    //             // Something else happened
    //             console.error('Error:', error.message);
    //         }
    //         console.error('Config:', error.config);
    //     }
    // });


    return (
        <div>
            <h1>Quotations</h1>
            {/* {.map(info => (
                <img src={info.imageURL} alt=''></img>
            ))} */}
            {/* table form */}
        </div>
    )
}

export default UserQuotations;
