import React from 'react';
import './registerpage.css';
import { useForm } from "react-hook-form";
import { useNavigate } from "react-router-dom";
import axios from 'axios';

const Register = () => {
    const navigate = useNavigate();
    

    
    const onSubmit = async (e) => {

        e.preventDefault();
        if (!validateForm()) {
            return; // Stop submission if validation fails
        }

        const { username, email, password } = formData;

        try {
            const response = await axios.post('http://localhost:5000/api/route', formData);
            console.log('Data submitted:', response.data);
        } catch (error) {
            console.error('Error posting data:', error);
        }

    };


    // const onSubmit = async (inputData) => {
    //     console.log(inputData);

    //     const data = { key: 'value' };  // Assuming this is the data you intend to send
    //     let response;

    //     if (data.key && typeof data.key === 'string') {
    //         try {
    //             response = await fetch('http://localhost:5000/api/route', {
    //                 method: 'POST',
    //                 headers: {
    //                     'Content-Type': 'application/json',
    //                 },
    //                 body: JSON.stringify(data)
    //             });

    //             if (response.ok) {  // Check if the HTTP status code is 200-299
    //                 console.log('Data sent to server successfully');
    //                 navigate('/'); // Adjust the route as necessary (e.g., to homepage or dashboard)
    //             } else {
    //                 console.error('Server responded with a non-200 status: ', response.status);
    //                 throw new Error('Something went wrong on api server!');
    //             }
    //         } catch (error) {
    //             console.error('Network or server error: ', error.message);
    //             // Optionally handle errors in UI, e.g., show a notification or error message
    //         }
    //     } else {
    //         console.error('Invalid data format');
    //         // Optionally handle data validation errors in UI
    //     }
    // };


    return (
        <div className='register-form-bg'>
            <h3>Register your account</h3>
            <form onSubmit={onSubmit}>
                <div className='register-account-inputs'>
                    <h5>Your username</h5>
                    <input name="username" value={formData.username} onChange={handleChange} type='text' placeholder='Doe' />
                    {errors.username && <span style={{ color: 'red' }}>{errors.username}</span>}
                </div>
                <div className='register-account-inputs'>
                    <h5>Your email</h5>
                    <input name="email" value={formData.email} onChange={handleChange} type='email' placeholder='xxx@mailbox.com' />
                    {errors.email && <span style={{ color: 'red' }}>{errors.email}</span>}
                </div>
                <div className='register-account-inputs'>
                    <h5>Your password</h5>
                    <input name="password" value={formData.password} onChange={handleChange} type='password' />
                    {errors.password && <span style={{ color: 'red' }}>{errors.password}</span>}
                </div>
                <div className='register-account-inputs'>
                    <h5>Repeat password</h5>
                    <input name="passwordRepeat" value={formData.passwordRepeat} onChange={handleChange} type='password' />
                    {errors.passwordRepeat && <span style={{ color: 'red' }}>{errors.passwordRepeat}</span>}
                </div>
                <div className='register-center'>
                    <input {...register('terms', { required: true })} className="form-check-input" type="checkbox" id="checkboxNoLabel" value="" aria-label="..."></input>
                    <label className="form-check-label" for="inlineCheckbox1">I agree with the Terms & Conditions</label>
                    {errors.terms && <span style={{ color: 'red' }}>You must agree to the terms</span>}
                </div>
                <div className='register-center'>
                    <button type="submit" className="btn btn-primary">Register Account</button>
                </div>
            </form>
        </div>
    )
}

export default Register;
