import React, { useEffect, useState, useContext, createContext } from 'react';
import './wishlist.css';


const WishlistContext = createContext();

export const useWishlist = () => useContext(WishlistContext);

export const WishlistProvider = ({ children }) => {
    const [wishList1, setWishList1] = useState([]);
    const [wishList2, setWishList2] = useState([]);
    const [wishListCount1, setWishListCount1] = useState(0);
    const [wishListCount2, setWishListCount2] = useState(0);

    // Function to add to wishlist 1
    const addToWishList1 = (item) => {
        setWishList1(current => [...current, item]);
        setWishListCount1(current => current + 1);
    };

    // Function to add to wishlist 2
    const addToWishList2 = (item) => {
        setWishList2(current => [...current, item]);
        setWishListCount2(current => current + 1);
    };

    return (
        <WishlistContext.Provider value={{
            wishList1,
            wishList2,
            wishListCount1,
            wishListCount2,
            addToWishList1,
            addToWishList2
        }}>
            {children}
        </WishlistContext.Provider>
    );
};


const Wishlist = () => {

    const [testArray, setTestArray] = useState([]);
    const [filteredTestArray, setFilteredTestArray] = useState([]);

    useEffect(() => {
        fetch('./products.json')
            .then(response => response.json())
            .then(data => {
                const array = data.map(product => product);
                setTestArray(array);
                setFilteredTestArray(array.slice(0, 6));
            })
            .catch(error => console.error('Error fetching products:', error));
    }, []);

   
    const cancelItemWishlist = (index) => {
        const newArray = filteredTestArray.filter((item, idx) => idx !== index);
        setFilteredTestArray(newArray);
    }


    return (
        <div className='temp-palelime-bg'>
            <h1>WISHLIST</h1>
            <div className='white-bg'>
                <table className='outlined-table'>
                    <thead>
                        <tr>
                            <th>Product</th>
                            <th>Product ID</th>
                            <th>Product Name</th>
                            <th>Cancel</th>
                        </tr>
                    </thead>
                    {filteredTestArray.length > 0 ? (
                        filteredTestArray.map((product, index) => (
                            <tbody key={index}>
                                <tr>
                                    <td><img src={product.imageURL} alt='' style={{ width: '100px', height: 'auto' }} /></td>
                                    <td>{product.productID}</td>
                                    <td>{product.productName}</td>
                                    <td className='cancel-product-wishlist' onClick={() => cancelItemWishlist(index)}>X</td>
                                </tr>
                            </tbody>
                        ))
                    ) : (
                        <p>No items in Wishlist.</p>
                    )}
                </table>
            </div>
        </div>
    )
}

export default Wishlist;
