import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UserPurchases = () => {
    // function 1, fetch data 1

    const [rentals, setRentals] = useState([]);

    useEffect(() => {
        const fetchRentals = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/rentalinfo');
                console.log('Rental data retrieved: ', response.data);
                // setRentals according to email logged in
                const emailLoggedIn = JSON.parse(localStorage.getItem('email'));
                console.log(emailLoggedIn); 
                setRentals(response.data.filter(item => item.rental_email.replace(/["\\]/g, '') === emailLoggedIn));
                console.log(rentals);

            } catch (error) {
                if (error.response && error.response.status === 400) {
                    console.error('Error:', error.response.data);
                } else {
                    console.error('Error logging in:', error.response ? error.response.data : error.message);
                }
            }
        }

        fetchRentals();
    }, []);


    const generateIndex = () => {
        return;
    }


    return (
        <div>
            <h1>Purchases</h1>
            <div>
                <table>

                    <thead>
                        <tr>
                            <th>No.</th>
                            <th>Rental <br></br> ID</th>
                            <th>Product Name</th>
                            <th>Selected Quantity</th>
                        </tr>
                    </thead>

                    <tbody>
                        <tr>
                            <td></td>
                            <td></td>
                            <td></td>
                            <td></td>
                        </tr>
                    </tbody>

                </table>
            </div>

            <h1>Rentals</h1>
            <div>
                <table>

                    <thead>
                        <tr>
                            <th>No.</th>
                            <th>Purchase <br></br> ID</th>
                            <th>Product Name</th>
                            <th>Selected Quantity</th>
                        </tr>
                    </thead>

                    <tbody>
                        <tr>
                            <td></td>
                            <td></td>
                            <td></td>
                            <td></td>
                        </tr>
                    </tbody>

                </table>
            </div>

        </div>
    )
}

export default UserPurchases;
