import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UserQuotations = () => {

    const [accountInfo, setAccountInfo] = useState([]);

    document.addEventListener('DOMContentLoaded', () => {
        fetch('/data', {timeout: 5000})
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }
                return response.json();
            })
            .then(data => {
                const dataContainer = document.getElementById('data-container');
                data.forEach(row => {
                    const rowDiv = document.createElement('div');
                    rowDiv.textContent = JSON.stringify(row);
                    dataContainer.appendChild(rowDiv);
                });
            })
            .catch(error => {
                if (error.response) {
                    // Server responded with a status other than 200 range
                    console.error('Server responded with an error:', error.response.data);
                } else if (error.request) {
                    // Request was made but no response received
                    console.error('No response received:', error.request);
                } else {
                    // Something else happened
                    console.error('Error:', error.message);
                }
                console.error('Config:', error.config);
            });
    });
    

    useEffect(() => {
        const fetchAccountInfo = async () => {
            try {
                const response = await axios.get('http://localhost:3001/api/accountinfo', {
                    timeout: 5000, // Set timeout to 5 seconds
                })
                console.log('Account Data:', response.data);
                setAccountInfo(response.data);
            } catch (error) {
                if (error.response) {
                    // Server responded with a status other than 200 range
                    console.error('Response error:', error.response.data);
                } else if (error.request) {
                    // Request was made but no response received
                    console.error('No response:', error.request);
                } else {
                    // Something else happened
                    console.error('Error:', error.message);
                }
                console.error('Config:', error.config);
            }
        };

        fetchAccountInfo();
    }, []);

    return (
        <div>
            <h1>Quotations</h1>
            {accountInfo.map(info => (
                <img src={info.imageURL} alt=''></img>
            ))}
            {/* table form */}
        </div>
    )
}

export default UserQuotations;
