import React, { useEffect, useState } from 'react';
import './wishlist.css';

const Wishlist = () => {

    const [testArray, setTestArray] = useState([]);
    const [filteredTestArray, setFilteredTestArray] = useState([]);

    useEffect(() => {
        fetch('./products.json')
            .then(response => response.json())
            .then(data => {
                const array = data.map(product => product);
                setTestArray(array);
                setFilteredTestArray(array.slice(0, 6));
            })
            .catch(error => console.error('Error fetching products:', error));
    }, []);

    return (
        <div className='temp-palelime-bg'>
            <h1>WISHLIST</h1>
            <div className='white-bg'>
                <table className='outlined-table'>
                    <thead>
                        <tr>
                            <th>Product</th>
                            <th>Product ID</th>
                            <th>Product Name</th>
                        </tr>
                    </thead>
                    {filteredTestArray.length > 0 ? (
                        filteredTestArray.map((product, index) => (
                            <tbody>
                                <tr key={index}>
                                    <td>{product.imageURL}</td>
                                    <td>{product.productID}</td>
                                    <td></td>
                                </tr>                    
                            </tbody>
                        ))
                    ) : (
                        <p>No items in Wishlist.</p>
                    )}
                </table>
            </div>
        </div>
    )
}

