import React, { useEffect, useState } from 'react';
import './rentalform.css';
import { json, useLocation, useNavigate } from 'react-router-dom';
import { keyboard } from '@testing-library/user-event/dist/keyboard';

const PurchaseForm = () => {
    const navigate = useNavigate(); // go to purchase history log (not yet establiushed)
    const location = useLocation();
    const productInfoForPurchase = location.state?.productInfoForPurchase || []; //ok (from bakeware directly)
    // sessionStorage get productName (from rental page)

    // if navigate to anywhere other than clicking that 'Submit' button , delete sessionStorage 'transfer'
    // const useThis = any data available in either forPurchase|sessionStorage from rental 


    // Test whether your data is stored / retrieved properly: 
    // 1: 
    console.log(JSON.stringify(productInfoForPurchase)); 

    // 2: 
    console.log(sessionStorage.getItem(''))

    const [productInfo, setProductInfo] = useState([]);

    useEffect(() => {
        if (sessionStorage.getItem('transfer') !== null) {
            // Get info from products.json
            fetch('./products.json')
                .then(response => response.json())
                .then(data => {
                    const filteredInfo = data.filter(prdt =>
                        prdt.productName.map(name => name.trim()).includes(sessionStorage.getItem('transfer'))
                    );

                    setProductInfo(filteredInfo);
                })
                .catch(error => console.error('Error fetching products:', error));
        } else {
            setProductInfo(JSON.stringify(productInfoForPurchase));

        }
    }, []);


    console.log("test if it works: ", productInfo);


    return (
        <div>

        </div>
    )
}

export default PurchaseForm;
