    const location = useLocation();
    const productInfoForPurchase = location.state?.productInfoForPurchase || [];

    // if navigate to anywhere other than clicking that 'Submit' button , delete sessionStorage 'transfer'
    // const useThis = any data available in either forPurchase|sessionStorage from rental 


    const [productInfo, setProductInfo] = useState([]);

