            fetch('./products.json')
            .then(response => response.json())
            .then(data => {
                const filteredInfo = data.filter(prdt =>
                    prdt.productName.map(name => name.trim()).includes(sessionStorage.getItem('transfer'))
                );

                setBakewareProducts(filteredBakewareProducts);
            })
            .catch(error => console.error('Error fetching products:', error));
        } else {
            setProductInfo(productInfoForPurchase);

        }
    }, []);



    return (
        <div>

        </div>
    )
}

