import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UserQuotations = () => {

    const [quotations, setQuotations] = useState([]);
    const [productNames, setProductNames] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            console.log('Making request...');
            try {
                const response = await axios.get('http://localhost:5000/api/accountinfo');
                console.log('Data retrieved: ', response.data);
                setQuotations(response.data);
            } catch (error) {
                if (error.response && error.response.status === 400) {
                    console.error('Error:', error.response.data);
                } else {
                    console.error('Error logging in:', error.response ? error.response.data : error.message);
                }
            }
        };

        fetchData();
    }, []);


    const indexCount = (index) => {
        return index + 1;
    }

    const datePart = (string) => {
        return string.split('T')[0];
    }


    useEffect(() => {
        const fetchProductNames = async () => {
            const names = await Promise.all(
                quotations.map(async (quotation) => {
                    const productName = await getProductName(quotation.productID);
                    return { ...quotation, productName };
                })
            );
            setProductNames(names);
        };

        if (quotations.length > 0) {
            fetchProductNames();
        }
    }, [quotations]);


    const getProductName = async (productID) => {
        try {
            const response = await fetch('./products.json');
            const data = await response.json();
            const matchedItem = data.find(individual => individual.productID === productID);
            return matchedItem ? matchedItem.productName : 'Unknown';
        } catch (error) {
            console.error('Error fetching product data:', error);
            return 'Unknown';
        }
    }


    return (
        <div>
            <h1>Quotations</h1>

            <div className='white-bg'>
                <table>
                    <thead>
                        <tr>
                            <th>Index</th>
                            <th>Quotation_ID</th>
                            <th>Date of Request</th>
                            <th>Product Names</th>
                        </tr>
                    </thead>

                    <tbody>
                        {(quotations && quotations.length > 0) ? (
                            quotations.map((quotation, index) => (
                                <tr key={indexCount(index)}>

                                    <td>{indexCount(index)}</td>

                                    <td>{quotation.quotation_id}</td>

                                    <td>{datePart(quotation.date_of_creation)}</td>

                                    {/* retrieve each prdt name from products.json file */}
                                    <td>{(quotations.map(quotation => getProductNames(quotation)))}</td>
                                </tr>
                            ))

                        ) : (
                            <tr>
                                <td colSpan="5">No data available</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>

            {
                quotations.map((quotation, index) => (
                    <div key={index}>
                        {/* <p>ID: {quotation.quotation_id}</p>
                    <p>Date: {quotation.date_of_creation}</p>
                    <p>Product IDs: {quotation.productid_combination}</p>
                    <p>Quantity: {quotation.quotation_quantity}</p> */}
                    </div>
                ))
            }


        </div >
    )
}

export default UserQuotations;
