import React from 'react';
import './rentalform.css';
import { useLocation, useNavigate } from 'react-router-dom';

const PurchaseForm = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const productInfoForPurchase = location.state?.productInfoForPurchase || []; //ok
    const productInfoFromRental = location.state?.productInfoFromRental || []; // not ok


// if navigate to anywhere other than clicking that 'Submit' button , delete sessionStorage 'purchaseItem'
// const useThis = any data available in either forPurchase|fromRental 



    return (
        <div>
            {productInfoFromRental.length > 0 ? (
                <div>
                    <h2>Product Information</h2>
                    <ul>
                        {productInfoFromRental.map((product, index) => (
                            <li key={index}>
                                <p>Product ID: {product.productID}</p>
                                <p>Product Name: {product.productName}</p>
                                <p>Price: {product.price}</p>
                                <p>Rent: {product.rent}</p>
                                <img src={product.imageURL} alt={product.productName} />
                            </li>
                        ))}
                    </ul>
                </div>
            ) : (
                <p>No product information available.</p>
            )}
        </div>
    )
}

export default PurchaseForm;
